<template>
  <div class="container">
    <div>
      <h1 class="title">
        ERC20 Contract Factory
      </h1>
      <div>
      	<h2 class="subtitle">
	        Tinker with contract factory
	     </h2>
       <div class="content">
          <br>
          <a
            target="_blank" 
            @click="goEtherscan(ethereumAddress)">
            Wallet Address: {{ this.ethereumAddress }}
          </a>
          <div
            v-if="!contractFactoryAddress && !showLoading" 
            class="links">
            <a
              @click="launchFactory()"
              class="button--green"
            >
              Launch Contract Factory
            </a>
          </div>
          <div
            v-else
          ><br>
            <a
              target="_blank"
              @click="goEtherscan(contractFactoryAddress)">
              Contract Factory Address: {{ this.contractFactoryAddress }}
            </a>
            <br>
            
          </div>
          <br>
          <form v-if="!launchHash && ethereumAddress && contractFactoryAddress && !showLoading" name="contact" action="" method="post">
            <label for="name">
              Name:
            </label>
            <input v-model="name" />
            <br>
            <label for="symbol">
              Symbol:
            </label>
            <input v-model="symbol" />
            <br>
            <label for="totalSupply">
              Total Supply:
            </label>
            <input v-model="totalSupply"/>
            <br>
            <label for="decimals" >
              Decimals:
            </label>
            <input v-model="decimals" ></input>
            <br><br>          
          </form>
          <div
            v-if="!launchHash && ethereumAddress && contractFactoryAddress && !showLoading" 
            class="links">
            <a
              @click="launchToken()"
              class="button--green"
            >
              Launch Token
            </a>
          </div>
          <div
            v-else
          ><br>
            <a v-if="launchHash"
              target="_blank" 
              @click="goEtherscan(launchHash)">
              New Contract Hash: {{ this.launchHash }}
            </a>
          </div>
          <br>
          <form v-if="launchHash && !showLoading" name="send" action="" method="post">
            <label for="recipientAddress">
              Send to:
            </label>
            <input v-model="recipientAddress" />
            <br>
            <label for="amount">
              Amount:
            </label>
            <input v-model="amount" />
            <br><br>    
            <div 
              class="links">
              <a
                @click="sendToken()"
                class="button--green"
              >
                Send {{this.amount}} of {{ this.symbol }} tokens to: {{ this.recipientAddress}}
              </a>
            </div>      
          </form>
          
          <div
            v-if="showLoading"
          >
            <img 
              src="../assets/gif/loading.gif" 
              alt >
          </div>
          <div 
            v-if="contractFactoryAddress"
            class="links">
            <a
              @click="resetLocalStorage()"
              class="button--green"
            >
              Start From Scratch
            </a>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>

import MetamaskHandler from "./metamaskHandler"

export default {
  extends: MetamaskHandler,
  components: {},
  async mounted() {
    //localStorage.contractFactoryAddress = '0x471FE61C929c03F534E32af51509DA308D911C3f' original factory address
    if (localStorage.ERC20launchAddress) this.launchAddress = localStorage.ERC20launchAddress
    if (localStorage.ERC20launchHash) this.launchHash = localStorage.ERC20launchHash
    if (localStorage.recipientAddress) this.recipientAddress = localStorage.recipientAddress
    if (localStorage.ERC20contractFactoryAddress) this.contractFactoryAddress = localStorage.ERC20contractFactoryAddress
  },
  methods: {
    async initializeWallet(){
      //this.wallet = new this.ethers.Wallet.createRandom()
      //this.wallet = new this.ethers.Wallet.fromMnemonic('wrist great knee profit inject clutch perfect purse faith lens vacuum world')
      //this.wallet = this.wallet.connect(this.ethereumProvider)
    },
    async launchFactory(){

      let factory = new this.ethers.ContractFactory(this.abi, this.bytecode, this.signer);
      let contract = await factory.deploy();

      this.showLoading = true
      await contract.deployed()
      this.showLoading = false

      this.contractFactoryAddress = localStorage.ERC20contractFactoryAddress = contract.address
      //console.log(contract.deployTransaction.hash);
    },
  	async launchToken(){

      let factory = new this.ethers.Contract(
        this.contractFactoryAddress, 
        this.abi, 
        this.signer
      )
      let tx = await factory.createEIP20(
        this.totalSupply,
        this.name,
        this.decimals,
        this.symbol 
      )
      this.showLoading = true
      let val = await tx.wait()
      this.showLoading = false

      this.launchHash = localStorage.ERC20launchHash = JSON.stringify(tx.hash).replace(/['"]+/g, '')
      this.launchAddress = localStorage.ERC20launchAddress = val.events[0].address

      if (!this.recipientAddress){
        let recipient = new this.ethers.Wallet.createRandom()
        this.recipientAddress = localStorage.recipientAddress = recipient.address
      }
  	},
    async sendToken(){

      let abi = [
        'function balanceOf(address who) external view returns (uint256)',
        'function transfer(address to, uint256 value) external returns (bool)'
      ]

      let token = new this.ethers.Contract(this.launchAddress, abi, this.signer)

      let amount1 = await token.balanceOf(this.ethereumAddress)
      let amount2 = await token.balanceOf(this.recipientAddress)
      console.log('Balance #1: ', this.ethers.utils.formatEther(amount1))
      console.log('Balance #2: ', this.ethers.utils.formatEther(amount2))

      
      let tx = await token.transfer(this.recipientAddress,this.ethers.utils.parseEther(this.amount.toString()))

      this.showLoading = true
      let val = await tx.wait()
      this.showLoading = false

      amount1 = await token.balanceOf(this.ethereumAddress)
      amount2 = await token.balanceOf(this.recipientAddress)
      console.log('Balance #1: ', this.ethers.utils.formatEther(amount1))
      console.log('Balance #2: ', this.ethers.utils.formatEther(amount2))
    },
    resetLocalStorage(){
      localStorage.clear()
      location.reload()
    },
    goEtherscan(param){
      if (param.length == 42){
        window.open('https://rinkeby.etherscan.io/address/'+param)
      }else{
        window.open('https://rinkeby.etherscan.io/tx/'+param)
      }
    }
  },
  data() {
    return {
      contractFactoryAddress: null,
      name: 'tokenA',
      symbol: 'TKA',
      totalSupply: '1000000000000000000000000000',
      decimals: 18,
      launchHash: null,
      launchAddress: null,
      recipientAddress: null,
      amount: 100,
      showLoading: false,
      bytecode: '',

      abi: [
      {
        "inputs": [],
        "stateMutability": "nonpayable",
        "type": "constructor"
      },
      {
        "inputs": [
          {
            "internalType": "uint256",
            "name": "_initialAmount",
            "type": "uint256"
          },
          {
            "internalType": "string",
            "name": "_name",
            "type": "string"
          },
          {
            "internalType": "uint8",
            "name": "_decimals",
            "type": "uint8"
          },
          {
            "internalType": "string",
            "name": "_symbol",
            "type": "string"
          }
        ],
        "name": "createEIP20",
        "outputs": [
          {
            "internalType": "address",
            "name": "",
            "type": "address"
          }
        ],
        "stateMutability": "nonpayable",
        "type": "function"
      },
      {
        "inputs": [
          {
            "internalType": "address",
            "name": "",
            "type": "address"
          },
          {
            "internalType": "uint256",
            "name": "",
            "type": "uint256"
          }
        ],
        "name": "created",
        "outputs": [
          {
            "internalType": "address",
            "name": "",
            "type": "address"
          }
        ],
        "stateMutability": "view",
        "type": "function"
      },
      {
        "inputs": [
          {
            "internalType": "address",
            "name": "",
            "type": "address"
          }
        ],
        "name": "isEIP20",
        "outputs": [
          {
            "internalType": "bool",
            "name": "",
            "type": "bool"
          }
        ],
        "stateMutability": "view",
        "type": "function"
      }
    ]
    }
  }
}
</script>

