<template>
  <div class="container">
    <div>
      <h1 class="title">
        ERC721 Contract
      </h1>
      <div>
      	<h2 class="subtitle">
	        Tinker with NFT contract factory
	     </h2>
       <div class="content">
          <div>
            <br>
            <a
              target="_blank" 
              @click="goEtherscan(ethereumAddress)">
              Wallet Address: {{ this.ethereumAddress }}
            </a>
          </div>
          <br>
          <div
            v-if="!contractFactoryAddress && !showLoading" 
            class="links">
            <a
              @click="launchFactory()"
              class="button--green"
            >
              Launch Contract Factory
            </a>
          </div>
          <div
            v-else
          ><br>
            <a
              target="_blank"
              @click="goEtherscan(contractFactoryAddress)">
              Contract Factory Address: {{ this.contractFactoryAddress }}
            </a>
            <br>
            <br>
          </div>
          <form v-if="ethereumAddress && contractFactoryAddress && !launchAddress" name="contact" action="" method="post">
            <label for="name">
              Name:
            </label>
            <input v-model="name"/>
            <br>
            <label for="symbol">
              Symbol:
            </label>
            <input v-model="symbol"/>
            <br>       
          </form>
          <div
            v-if="ethereumAddress && contractFactoryAddress && !launchAddress && !showLoading" 
            class="links">
            <a
              @click="launchToken()"
              class="button--green"
            >
              Launch Token
            </a>
          </div>
          <div
            v-else
          ><br>
            <a v-if="launchHash"
              target="_blank" 
              @click="goEtherscan(launchHash)">
              New Contract Hash: {{ this.launchHash }}
            </a>
            <br>
            <br>
            <a v-if="launchAddress"
              target="_blank" 
              @click="goEtherscan(launchAddress)">
              New Contract Address: {{ this.launchAddress }}
            </a>
            <div 
              v-if="launchAddress && !tokenBalance"
              class="links">
              <a
                @click="checkBalance()"
                class="button--green"
                isable
              >
                Check NFT Balance
              </a>
            </div>
          </div>
          <div
            v-if="launchHash && !showLoading" 
            class="links">
            <a
              @click="mintToken()"
              class="button--green"
            >
              Mint A Token
            </a>
            <br>
            <br>
          </div>
          <form v-if="launchHash && !showLoading && tokenBalance" name="send" action="" method="post">
            <label for="recipientAddress">
              Send to:
            </label>
            <input v-model="recipientAddress" />
            <br>
            <label for="tokenID">
              Token ID:
            </label>
            <input v-model="tokenID" />
            <br>   
            <div 
              class="links">
              <a
                @click="sendToken()"
                class="button--green"
                isable
              >
                Send {{ this.symbol }} token id {{this.tokenID}} to: {{ this.recipientAddress}}
              </a>
            </div>      
          </form>
          <div
            v-if="showLoading"
          >
            <img 
              src="../assets/gif/loading.gif" 
              alt >
          </div>
          <div 
            v-if="contractFactoryAddress"
            class="links">
            <a
              @click="resetLocalStorage()"
              class="button--green"
            >
              Start From Scratch
            </a>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>

import MetamaskHandler from "./metamaskHandler"

export default {
  extends: MetamaskHandler,
  components: {},
  async mounted() {
    //localStorage.contractFactoryAddress = '0x471FE61C929c03F534E32af51509DA308D911C3f' original factory address
    if (localStorage.ERC721launchAddress) this.launchAddress = localStorage.ERC721launchAddress
    if (localStorage.ERC721launchHash) this.launchHash = localStorage.ERC721launchHash
    if (localStorage.recipientAddress) this.recipientAddress = localStorage.recipientAddress
    if (localStorage.ERC721TokenId) this.tokenID = localStorage.ERC721TokenId
    if (localStorage.ERC721contractFactoryAddress) this.contractFactoryAddress = localStorage.ERC721contractFactoryAddress
  },
  methods: {
    async launchFactory(){

      let factory = new this.ethers.ContractFactory(this.abi, this.bytecode, this.signer);
      let contract = await factory.deploy();

      this.showLoading = true
      await contract.deployed()
      this.showLoading = false

      this.contractFactoryAddress = localStorage.ERC721contractFactoryAddress = contract.address
      //console.log(contract.deployTransaction.hash);
    },
    async launchToken(){

      let factory = new this.ethers.Contract(
        this.contractFactoryAddress, 
        this.abi, 
        this.signer
      )
      let tx = await factory.createEIP721(
        this.name,
        this.symbol 
      )

      this.showLoading = true
      let val = await tx.wait()
      this.showLoading = false
      this.launchHash = localStorage.ERC721launchHash = JSON.stringify(tx.hash).replace(/['"]+/g, '')
      this.launchAddress = localStorage.ERC721launchAddress= val.events[0].address

      if (!this.recipientAddress){
        this.createRecipient()
      }
    },
    async createRecipient(){
      let recipient = new this.ethers.Wallet.createRandom()
      this.recipientAddress = localStorage.ERC721recipientAddress = recipient.address
    },
    async checkBalance(){
      if (!this.recipientAddress){
        await this.createRecipient()
      }
      if (localStorage.ERC721launchAddress){
        let token = new this.ethers.Contract(this.launchAddress, this.tokenABI, this.signer)
        let amount1 = await token.balanceOf(this.ethereumAddress)
        amount1 = amount1.toNumber()
        let tot = await token.totalSupply()
        let idByIndex = await token.tokenByIndex(tot.toNumber() - 1)

        let name = await token.name()
        let symbol = await token.symbol()
        let uri = await token.tokenURI(idByIndex.toNumber())

        alert("Your "+name.toString()+" ("+symbol+") Balance is: "+amount1+" With total supply of: "+tot.toNumber()+" With Token ID at: "+idByIndex.toNumber())
        
        this.tokenID = localStorage.ERC721TokenId = idByIndex.toNumber() 
        this.tokenBalance = amount1
        return amount1
      }
      return 0
    },
    async mintToken(){
      this.tokenID++
      let token = new this.ethers.Contract(this.launchAddress, this.tokenABI, this.signer)
      let uri = 'https://i.picsum.photos/id/'+this.tokenID+'/200/200.jpg'
      let tx = await token.mint(this.ethereumAddress, uri)

      this.showLoading = true
      let val = await tx.wait()
      this.showLoading = false

      this.tokenBalance = await this.checkBalance()

    },
    async sendToken(){

      let token = new this.ethers.Contract(this.launchAddress, this.tokenABI, this.signer)

      let amount1 = await token.balanceOf(this.ethereumAddress)
      let amount2 = await token.balanceOf(this.recipientAddress)
      console.log('Balance #1: ', amount1.toNumber())
      console.log('Balance #2: ', amount2.toNumber())

      //let approve = await token.approve(this.recipientAddress, this.tokenID.toString())
      let tx = await token.safeTransferFrom(this.ethereumAddress, this.recipientAddress, this.tokenID.toString())

      this.showLoading = true
      let val = await tx.wait()
      this.showLoading = false

      amount1 = await token.balanceOf(this.ethereumAddress)
      amount2 = await token.balanceOf(this.recipientAddress)
      console.log('Balance #1: ', amount1.toNumber())
      console.log('Balance #2: ', amount2.toNumber())

      await this.checkBalance()
    },
    resetLocalStorage(){
      localStorage.clear()
      location.reload()
    },
    goEtherscan(param){
      if (param.length == 42){
        window.open('https://rinkeby.etherscan.io/address/'+param)
      }else{
        window.open('https://rinkeby.etherscan.io/tx/'+param)
      }
    }
  },
  data() {
    return {
      contractFactoryAddress: null,
      name: 'Verify Non-Fungible Token',
      symbol: 'VNFT',
      launchHash: null,
      launchAddress: null,
      recipientAddress: null,
      tokenID: 1,
      tokenBalance: null,
      showLoading: false,
      tokenABI: [
        'function balanceOf(address owner) public view returns (uint256)',
        'function safeTransferFrom(address from, address to, uint256 tokenId) public',
        'function approve(address to, uint256 tokenId) public',
        'function mint(address to, string tokenURI) public returns (uint256)',
        //metadata
        'function name() external view returns (string memory)',
        'function symbol() external view returns (string memory)',
        'function tokenURI(uint256 tokenId) external view returns (string memory)',
        //enumeration
        'function totalSupply() external view returns (uint256)',
        'function tokenOfOwnerByIndex(address owner, uint256 index) external view returns (uint256 tokenId)',
        'function tokenByIndex(uint256 index) external view returns (uint256)'

      ],
      bytecode: '',

      abi: [
        {
          "inputs": [],
          "stateMutability": "nonpayable",
          "type": "constructor"
        },
        {
          "inputs": [
            {
              "internalType": "string",
              "name": "name",
              "type": "string"
            },
            {
              "internalType": "string",
              "name": "symbol",
              "type": "string"
            }
          ],
          "name": "createEIP721",
          "outputs": [
            {
              "internalType": "address",
              "name": "",
              "type": "address"
            }
          ],
          "stateMutability": "nonpayable",
          "type": "function"
        },
        {
          "inputs": [
            {
              "internalType": "address",
              "name": "",
              "type": "address"
            },
            {
              "internalType": "uint256",
              "name": "",
              "type": "uint256"
            }
          ],
          "name": "created",
          "outputs": [
            {
              "internalType": "address",
              "name": "",
              "type": "address"
            }
          ],
          "stateMutability": "view",
          "type": "function"
        },
        {
          "inputs": [
            {
              "internalType": "address",
              "name": "",
              "type": "address"
            }
          ],
          "name": "isEIP721",
          "outputs": [
            {
              "internalType": "bool",
              "name": "",
              "type": "bool"
            }
          ],
          "stateMutability": "view",
          "type": "function"
        }
      ]
    }
  }
}
</script>

