            Launched Wallet Address: {{ this.launchedWalletAddress }}
          </a>
          <!-- br>
          <br>
          <a 
            @click="launchWallet()"
            class="button--green"
          >
            Launch Another Wallet
          </a -->
        </div>
        <div
          v-if="launchedWalletAddress && !showLoading && !minted" 
          class="links">
          <a
            @click="dropMyCrypto()"
            class="button--green"
          >
            Drop Crypto For Launched Wallet
          </a>
        </div> 
        <form v-if="minted && !showLoading" name="send" action="" method="post">
          <br><br>
          <label for="recipientAddress">
            Send to:
          </label>
