          <br><br>    
          <div 
            class="links">
            <a
              @click="sendToken()"
              class="button--green"
            >
              Send {{this.amount}} of {{ this.tokenId }} tokens to: {{ this.recipientAddress}}
            </a>
          </div>    
        </form>     
        <div
          v-if="showLoading"
        >
          <img 
            src="../assets/gif/loading.gif" 
            alt >
        </div>
        <div 
            v-if="managerContractAddress"
            class="links">
            <br>
            <br>
            <a
              @click="resetLocalStorage()"
              class="button--green"
            >
              Start From Scratch
            </a>
          </div>
      </div>
    </div>
