<script>
import MetamaskHandler from "./metamaskHandler"
export default {
  extends: MetamaskHandler,
  components: {},
  data() {
    return {
      ethereumAddress: null
    }
  },

  async mounted() {
    if (localStorage.managerContractAddress) this.managerContractAddress = localStorage.managerContractAddress
    if (localStorage.launchedWalletAddress) this.launchedWalletAddress = localStorage.launchedWalletAddress
    if (localStorage.managerContractMinted) this.minted = localStorage.managerContractMinted 
    if (localStorage.managedContractRecipientAddress) this.recipientAddress = localStorage.managedContractRecipientAddress 

  },
  methods: {
    async launchManagerContract(){

      let factory = new this.ethers.ContractFactory(this.abi, this.bytecode, this.signer);
      let contract = await factory.deploy();

      this.showLoading = true
      await contract.deployed()
      this.showLoading = false

      this.managerContractAddress = localStorage.managerContractAddress = contract.address
      //console.log(contract.deployTransaction.hash);
    },
    async launchWallet(){

      let token = new this.ethers.Contract(
        this.managerContractAddress, 
        this.abi, 
        this.signer
      )
      this.showLoading = true
      let tx = await token.launchL2Account()
      let val = await tx.wait()
      this.showLoading = false
      this.launchedWalletAddress = localStorage.launchedWalletAddress = val.events[0].args['account']

      this.showLoading = true
      //Verifies that new wallet has approved the manager contract to perform transactions on its behalf
      tx = await token.isApprovedForAll(this.launchedWalletAddress, this.managerContractAddress)
      this.showLoading = false
      console.log(tx)
    },
    async dropMyCrypto(){

      let token = new this.ethers.Contract(
        this.managerContractAddress, 
        this.abi, 
        this.signer
      )

      let amount = 1000000000

      let amt = this.ethers.utils.parseUnits(amount.toString(), parseInt(this.decimals))

      let tx = await token.mint(
        this.launchedWalletAddress,
        this.tokenId,
        amt.toString(),
        this.ethers.utils.formatBytes32String('')
      )

      if (!this.recipientAddress){
        let recipient = new this.ethers.Wallet.createRandom()
        this.recipientAddress = localStorage.managedContractRecipientAddress = recipient.address
      }

      this.showLoading = true
      let val = await tx.wait()
      this.showLoading = false

      let amount1 = await token.balanceOf(this.launchedWalletAddress, this.tokenId)
      console.log('Balance for ',this.launchedWalletAddress, this.ethers.utils.formatUnits(amount1, this.decimals))

      this.minted = localStorage.managerContractMinted = true

    },
    async sendToken(){

      let token = new this.ethers.Contract(this.managerContractAddress, this.abi, this.signer)

      let amount1 = await token.balanceOf(this.launchedWalletAddress, this.tokenId)
      let amount2 = await token.balanceOf(this.recipientAddress, this.tokenId)
      console.log('Balance #1: ', this.launchedWalletAddress, this.ethers.utils.formatUnits(amount1, this.decimals))
      console.log('Balance #2: ', this.recipientAddress, this.ethers.utils.formatUnits(amount2, this.decimals))
      
      let tx = await token.managedTransfer(this.launchedWalletAddress, this.recipientAddress,this.tokenId, this.ethers.utils.parseEther(this.amount.toString()), this.ethers.utils.formatBytes32String(this.data))
      this.showLoading = true
      let val = await tx.wait()
      this.showLoading = false
      console.log(tx)
      console.log(val)
      amount1 = await token.balanceOf(this.launchedWalletAddress, this.tokenId)
      amount2 = await token.balanceOf(this.recipientAddress, this.tokenId)
      console.log('Balance #1: ', this.launchedWalletAddress, this.ethers.utils.formatUnits(amount1, this.decimals))
      console.log('Balance #2: ', this.recipientAddress, this.ethers.utils.formatUnits(amount2, this.decimals))
    },
    resetLocalStorage(){
      localStorage.removeItem('managerContractAddress')
      localStorage.removeItem('launchedWalletAddress')
      localStorage.removeItem('managerContractMinted')
      localStorage.removeItem('managedContractRecipientAddress')
      location.reload()
    },
    goEtherscan(param){
      if (param.length == 42){
        window.open('https://rinkeby.etherscan.io/address/'+param)
      }else{
        window.open('https://rinkeby.etherscan.io/tx/'+param)
      }
    }
  },
