<template>
  <div class="container">
    <div>
      <h1 class="title">
        Manager Contract
      </h1>
      <h2 class="subtitle">
        Fundamental Manager Operations
      </h2>
      <div class="links">
        <br>
        <a
          target="_blank" 
          @click="goEtherscan(ethereumAddress)">
          Wallet Address: {{ this.ethereumAddress }}
        </a>
        <br>Fake Twitter ID: {{ this.twitterId }}
        <div
          v-if="!managerContractAddress && !showLoading" 
          class="links">
          <a
            @click="launchManagerContract()"
