<script>
import MetamaskHandler from "./metamaskHandler"
export default {
  extends: MetamaskHandler,
  components: {},
  data() {
    return {
      ethereumAddress: null
    }
  },
  async mounted() {
    if (localStorage.managerContractAddress) this.managerContractAddress = localStorage.managerContractAddress
    if (localStorage.launchedWalletAddress) this.launchedWalletAddress = localStorage.launchedWalletAddress
  },
  methods: {
    async launchManagerContract(){

      let factory = new this.ethers.ContractFactory(this.abi, this.bytecode, this.signer);
      let contract = await factory.deploy();

      this.showLoading = true
      await contract.deployed()
      this.showLoading = false

      this.managerContractAddress = localStorage.managerContractAddress = contract.address
      //console.log(contract.deployTransaction.hash);
    },
    async launchWallet(){

      let factory = new this.ethers.Contract(
        this.managerContractAddress, 
        this.abi, 
        this.signer
      )
      this.showLoading = true
      let tx = await factory.launchL2Account()
      let val = await tx.wait()
      this.showLoading = false
      this.launchedWalletAddress = localStorage.launchedWalletAddress = val.events[0].args['account']

      this.showLoading = true
      //Verifies that new wallet has approved the manager contract to perform transactions on its behalf
      tx = await factory.isApprovedForAll(this.launchedWalletAddress, this.managerContractAddress)
      this.showLoading = false
      console.log(tx)

    },
    async dropMyCrypto(){

      let token = new this.ethers.Contract(
        this.managerContractAddress, 
        this.abi, 
        this.signer
      )

      let amount = 1000000000
      let decimals = 18
      let tokenId = 0

      let amt = this.ethers.utils.parseUnits(amount.toString(), parseInt(decimals))

      let tx = await token.mint(
        this.launchedWalletAddress,
        tokenId,
        amt.toString(),
        this.ethers.utils.formatBytes32String('')
      )

      this.showLoading = true
      let val = await tx.wait()
      this.showLoading = false

      let amount1 = await token.balanceOf(this.launchedWalletAddress, tokenId)
      console.log('Balance for : ',this.launchedWalletAddress, this.ethers.utils.formatUnits(amount1, decimals))

    },
    resetLocalStorage(){
      localStorage.removeItem('managerContractAddress')
      localStorage.removeItem('launchedWalletAddress')
      location.reload()
    },
    goEtherscan(param){
      if (param.length == 42){
        window.open('https://rinkeby.etherscan.io/address/'+param)
      }else{
        window.open('https://rinkeby.etherscan.io/tx/'+param)
      }
    }
  },
