<template>
  <div class="container">
    <div>
      <h1 class="title">
        Validator Contract
      </h1>
      <h2 class="subtitle">
        Test Full Validation Pathway
      </h2>
      <div class="links">
        <br>
        <a
          target="_blank" 
          @click="goEtherscan(ethereumAddress)">
          Wallet Address: {{ this.ethereumAddress }}
        </a>
        <br>Fake Twitter ID: {{ this.twitterId }}
        <div
          v-if="!ValidatorContractAddress && !showLoading" 
          class="links">
          <a
            @click="launchValidatorContract()"
            class="button--green"
          >
            Launch Validator Contract
          </a>
        </div>
         <div
          v-if="ValidatorContractAddress && managerContractAddress && !showLoading" 
          class="links">
          <a
            target="_blank"
            @click="goEtherscan(ValidatorContractAddress)">
            Validator Contract Address: {{ this.ValidatorContractAddress }}
          </a>
          <br>
          <br>
          <a
            target="_blank"
            @click="goEtherscan(managerContractAddress)">
            Manager Contract Address: {{ this.managerContractAddress }}
          </a>
          <br>
         
        </div>
        
        <div
          v-if="ValidatorContractAddress &&managerContractAddress && !showLoading && !minted" 
          class="links">
          <a
            @click="dropMyCrypto()"
            class="button--green"
          >
            Drop Crypto
          </a>
        </div> 
        <form v-if="ValidatorContractAddress && minted && !showLoading" name="send" action="" method="post">
          <br><br>
          <label for="amount">
            Amount:
          </label>
          <input v-model="amount" />
          <br><br>    
          <div 
            class="links">
            <a
              @click="sendToken()"
              class="button--green"
            >
              Send {{this.amount}} of {{ this.tokenId }} tokens to random recipient.
            </a>
          </div>    
        </form>

        <div
          v-if="ValidatorContractAddress && managerContractAddress && !showLoading" 
          class="links">
          <a
            @click="depositERC20()"
            class="button--green"
          >
            Deposit ERC20
          </a>
          <a
            @click="depositERC721()"
            class="button--green"
          >
            Deposit ERC721
          </a>
        </div>      
         <div
          v-if="ValidatorContractAddress && ERC1155tokenId && !showLoading" 
          class="links">
          <a
            @click="withdrawERC20()"
            class="button--green"
          >
            Withdraw ERC20
          </a>
          <a
            @click="withdrawERC721()"
            class="button--green"
          >
            Withdraw ERC721
          </a>
        </div> 
        <div
          v-if="showLoading"
        >
          <img 
            src="../assets/gif/loading.gif" 
            alt >
        </div>
        <div 
            v-if="ValidatorContractAddress && managerContractAddress && !showLoading && minted"
            class="links">
            <br>
            <br>
            <a
              @click="dropMyCrypto(true)"
              class="button--green"
            >
              Re-Drop Crypto
            </a>
          </div>
        <div 
            v-if="ValidatorContractAddress"
            class="links">
            <br>
            <br>
            <a
              @click="resetLocalStorage()"
              class="button--green"
            >
              Start From Scratch
            </a>
          </div>
      </div>
    </div>
  </div>
</template>

<script>
import MetamaskHandler from "./metamaskHandler"
export default {
  extends: MetamaskHandler,
  components: {},
  data() {
    return {
      ethereumAddress: null
    }
  },

  async mounted() {
    if (localStorage.managerContractAddress) this.managerContractAddress = localStorage.managerContractAddress
    if (localStorage.launchedWalletAddress) this.launchedWalletAddress = localStorage.launchedWalletAddress
    if (localStorage.managerContractMinted) this.minted = localStorage.managerContractMinted 
    if (localStorage.managedContractRecipientAddress) this.recipientAddress = localStorage.managedContractRecipientAddress 
    if (localStorage.tokenId) this.tokenId = localStorage.ERC1155tokenId
    if (localStorage.ERC1155tokenId) this.ERC1155tokenId = localStorage.ERC1155tokenId

    if (localStorage.ValidatorContractAddress) this.ValidatorContractAddress = localStorage.ValidatorContractAddress

    if (localStorage.twitterId) {
      this.twitterId = localStorage.twitterId
    } else {
      this.twitterId = localStorage.twitterId = Math.round(Math.random() * 1000000000)
    }
    if (localStorage.recipientTwitterId) {
      this.recipientTwitterId = localStorage.recipientTwitterId
    } else {
      this.recipientTwitterId = localStorage.recipientTwitterId = Math.round(Math.random() * 1000000000)
    }
    
  },
  methods: {
    async launchValidatorContract(){

      let factory = new this.ethers.ContractFactory(this.abi, this.bytecode, this.signer);

      //URI goes in here in place of ''
      let contract = await factory.deploy('');

      this.showLoading = true
      let tx = await contract.deployed()
      this.showLoading = false
      
      this.ValidatorContractAddress = localStorage.ValidatorContractAddress = contract.address
      
      this.managerContractAddress = localStorage.managerContractAddress = await contract.getTokenManager()
      this.minted = localStorage.managerContractMinted = false
    },
    async dropMyCrypto(redrop=false){

      if(redrop){
        this.twitterId = localStorage.twitterId = Math.round(Math.random() * 1000000000)
      }

      let token = new this.ethers.Contract(
        this.ValidatorContractAddress, 
        this.abi, 
        this.signer
      )
      this.showLoading = true

      let twitterIds = [this.twitterId, 0, 0]
      let twitterNames = ['@fakeHandle'+this.tokenId, '', '']

      let tx = await token.validateCommand(twitterIds, twitterNames, 'https://i.picsum.photos/id/'+this.tokenId+'/200/200.jpg', true, 0, this.ethers.utils.formatBytes32String(this.data))
      let val = await tx.wait()
      this.showLoading = false
      console.log(tx)
      console.log(val)
      this.launchedWalletAddress = localStorage.launchedWalletAddress = '0x'+val.events[0].topics[1].substr(val.events[0].topics[1].length - 5);

      this.showLoading = true

      this.minted = localStorage.managerContractMinted = true
      this.tokenId = localStorage.ERC1155tokenId = await token.getTokenIdFromPlatformId(this.twitterId)
    },
    async sendToken(){

      let abi = [
        'function balanceOf(address who, uint256 tokenId) external view returns (uint256)'
      ]

      let tokenManager = new this.ethers.Contract(this.managerContractAddress, abi, this.signer)

      let amount1 = await tokenManager.balanceOf(this.launchedWalletAddress, this.tokenId)
      
      console.log('Balance #1: ', this.launchedWalletAddress, this.ethers.utils.formatUnits(amount1, this.decimals))
      if (this.recipientAddress){
        let amount2 = await tokenManager.balanceOf(this.recipientAddress, this.tokenId)
        console.log('Balance #2: ', this.recipientAddress, this.ethers.utils.formatUnits(amount2, this.decimals))
      }
      
      let randomRecipientTwitterId = Math.round(Math.random() * 1000000000)
      let twitterIds = [this.twitterId.toString(), randomRecipientTwitterId.toString(), '0']
      let twitterNames = ['@fakeHandle', '@randomFake2', '']
      let tx = await tokenManager.initCommand(
        twitterIds, twitterNames, '', false, 
        this.ethers.utils.parseEther(this.amount.toString()), 
        this.ethers.utils.formatBytes32String(this.data)
      )
      this.showLoading = true
      let val = await tx.wait()
      this.showLoading = false
      amount1 = await tokenManager.balanceOf(this.launchedWalletAddress, this.tokenId)

      this.recipientAddress = this.managedContractRecipientAddress = await tokenManager.getUserAccount(randomRecipientTwitterId);
      console.log(this.recipientAddress)
      console.log('Balance #1: ', this.launchedWalletAddress, this.ethers.utils.formatUnits(amount1, this.decimals))
      
      amount1 = await tokenManager.balanceOf(this.recipientAddress, this.tokenId)
      console.log('Balance #2: ', this.recipientAddress, this.ethers.utils.formatUnits(amount1, this.decimals))
    },
    resetLocalStorage(){
      localStorage.removeItem('ValidatorContractAddress')
      location.reload()
    },
    goEtherscan(param){
      if (param.length == 42){
        window.open('https://rinkeby.etherscan.io/address/'+param)
      }else{
        window.open('https://rinkeby.etherscan.io/tx/'+param)
      }
    }
  },
  data() {
    return {
      managerContractAddress: null,
      launchedWalletAddress: null,
      recipientAddress: null,
      showLoading: false,
      decimals: 18,
      data: '',
      minted: false,
      tokenId: 0,
      amount: 100,
      twitterId: null,
      recipientTwitterId: null,
      ERC1155tokenId: null,
      ValidatorContractAddress: '',
      bytecode: '',

    abi: [
  {
    "inputs": [
      {
        "internalType": "string",
        "name": "_uri",
        "type": "string"
      }
    ],
    "stateMutability": "nonpayable",
    "type": "constructor"
  },
  {
    "anonymous": false,
    "inputs": [
      {
        "indexed": false,
        "internalType": "address",
        "name": "",
        "type": "address"
      }
    ],
    "name": "NewTokenManager",
    "type": "event"
  },
  {
    "inputs": [
      {
        "internalType": "address",
        "name": "newTokenManager",
        "type": "address"
      }
    ],
    "name": "changeTokenManager",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "getTokenManager",
    "outputs": [
      {
        "internalType": "address",
        "name": "",
        "type": "address"
      }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [
      {
        "internalType": "address",
        "name": "newValidator",
        "type": "address"
      }
    ],
    "name": "setValidator",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [
      {
        "internalType": "address",
        "name": "oldValidator",
        "type": "address"
      }
    ],
    "name": "unsetValidator",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [
      {
        "internalType": "uint256[]",
        "name": "_twitterIds",
        "type": "uint256[]"
      },
      {
        "internalType": "string[]",
        "name": "_twitterNames",
        "type": "string[]"
      },
      {
        "internalType": "string",
        "name": "_fromImageUrl",
        "type": "string"
      },
      {
        "internalType": "bool",
        "name": "_isLaunch",
        "type": "bool"
      },
      {
        "internalType": "uint256",
        "name": "_value",
        "type": "uint256"
      },
      {
        "internalType": "bytes",
        "name": "_data",
        "type": "bytes"
      }
    ],
    "name": "validateCommand",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  }
]
    }
  }
}
</script>

